import { createRouter, createWebHistory } from 'vue-router'
import HomeView from '../views/HomeView.vue'
import Dashboard from '../views/Dashboard.vue'
import Register from '../views/Register.vue'
import Login from '../views/Login.vue'
import Upload from '../views/Upload.vue'
import Pending from '../views/Pending.vue'
import UserDashboard from '../views/user/UserDashboard.vue'
import Approved from '../views/Approved.vue'
import UserBUM from '../views/user/UserBUM.vue'
import UserValidator from '../views/user/UserValidator.vue'
import UserEncoder from '../views/user/UserEncoder.vue'
import Generate from '../views/user/Generate.vue'


const routes = [
  {
    path: '/',
    component: HomeView
  },
  {
    path: '/login',
    component: Login
  },
  {
    path: '/register',
    component: Register
  },
  {
    path: '/userdashboard',
    component: UserDashboard,
    meta: { requiresAuth: true } 
  },
  // {
  //   path: '/users',
  //   component: UserDashboard
  // },
  {
    path: '/updating',
    component: UserBUM,
    meta: { requiresAuth: true } 
  },
  {
    path: '/encoder',
    component: UserEncoder,
    meta: { requiresAuth: true } 
  },
  {
    path: '/admindashboard',
    component: Dashboard
  },
  {
    path: '/pending',
    component: Pending
  },
  {
    path: '/approved',
    component: Approved
  },
  {
    path: '/generate',
    component: Generate
  },
  // {
  //   path: '/table',
  //   name: 'table',
  //   component: Table
  // },
  {
    path: '/upload',
    component: Upload
  },
  // {
  //   path: '/validator/:entryId',
  //   name: 'validate',
  //   component: Validators,
  //   props: true,
  // },
  {
    path: '/validate/:entryId',
    name: 'validate', // Add this line
    component: UserValidator,
    meta: { requiresAuth: true } ,
    props: true, // Enable passing route params as props
    // props: route => ({ details: route.params.details || {} })

  },
  {
    path: '/about',
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue')
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

router.beforeEach((to, from, next) => {
  if (to.meta.requiresAuth && !isAuthenticated()) {
    next('/login'); // Redirect to login if not authenticated
  } else {
    next();
  }
});

function isAuthenticated() {
  // Check if the user is authenticated (e.g., check for the presence of a valid token)
  return localStorage.getItem('token') !== null;
}

export default router
